INSERT INTO `fs_destination` VALUES (1, 'Hà Nội là thủ đô và thành phố lớn nhất của Việt Nam. Với hơn 1.000 năm lịch sử, Hà Nội là một trung tâm văn hóa, chính trị và kinh tế quan trọng của đất nước. Thành phố này đồng thời cũng là một điểm đến du lịch phổ biến với nhiều di sản văn hóa và kiến trúc độc đáo.', NULL, '', 1, 'ha-noi', 'Hà Nội', ',1,', ',ha-noi,', NULL, 'Hồ Hoàn Kiếm - Phố cổ - Văn miếu Quốc Tử Giám', 'ho-hoan-kiemha-noi', 'images/detination/2023/06/original/hoguom_1687110006.jpg', NULL, NULL, NULL, '2023-06-07 21:12:39', '2023-06-21 12:45:42', NULL, '1', NULL, NULL, NULL, NULL, '1', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', '', '', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'admin', NULL, 'admin', '2023-06-07 21:13:26', '2023-06-21 12:45:42', '9', '9', NULL, NULL, NULL, NULL, '<p>Hà Nội có một vẻ đẹp đa dạng, từ những phố cổ rối ren và ngõ hẹp tại khu phố cổ, đến những tòa nhà cao tầng hiện đại và công viên rộng lớn. Văn hóa truyền thống của Hà Nội được thể hiện qua các di tích lịch sử và văn hóa, như Văn Miếu - Quốc Tử Giám (Đền Văn Miếu), Hoàng thành Thăng Long (Di sản thế giới UNESCO), chùa Một Cột và hồ Hoàn Kiếm.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Hồ Hoàn Kiếm</h3>\r\n\r\n<p><img alt=\"\" src=\"/upload_images/images/2023/06/18/hoguom1.jpg\" style=\"width: 100%;\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hà Nội cũng nổi tiếng với ẩm thực đa dạng và phong phú. Bạn có thể thưởng thức các món ăn đặc sản như phở, bún chả, nem rán và cà phê trứng. Khu phố cổ Hà Nội là nơi bạn có thể tìm thấy nhiều quán ăn đường phố và nhà hàng truyền thống.&nbsp;Ngoài ra, Hà Nội còn có nhiều bảo tàng, phòng trưng bày nghệ thuật, thị trấn nổi tiếng như Ba Đình Square (nơi có Lăng Chủ tịch Hồ Chí Minh) và Hồ Tây (hồ nước lớn nhất của thành phố). Cảnh quan tự nhiên xung quanh Hà Nội cũng rất đẹp, với các công viên và hồ nước như Công viên Thống Nhất, Công viên Hoàng Thành và Hồ Đền Lừ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/upload_images/images/2023/06/21/ph%C3%B4chanoi.jpg\" style=\"width: 100%;\" /></p>\r\n\r\n<p>Hà Nội có một sự pha trộn độc đáo giữa sự hiện đại và truyền thống, mang đến cho du khách một trải nghiệm đa dạng và thú vị. Thành phố này là một điểm đến hấp dẫn cho những ai muốn khám phá văn hóa, lịch sử và ẩm thực của Việt Nam.</p>');
INSERT INTO `fs_destination` VALUES (2, 'Một thị trấn nằm ở cao nguyên Lào Cai, miền bắc Việt Nam. Với khí hậu mát mẻ quanh năm, cảnh quan hùng vĩ và đa dạng văn hóa các dân tộc, Sapa đã trở thành một điểm đến du lịch phổ biến.', NULL, '', 6, 'sapa', 'Sapa', ',6,', ',sapa,', NULL, 'Sapa - Phố núi trong sương ', 'thai-binh', 'images/detination/2023/06/original/ruongbacthang2_1687285934.jpg', NULL, NULL, NULL, '2023-07-03 00:30:29', '2023-06-21 01:32:14', NULL, '1', NULL, NULL, NULL, NULL, '1', '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', '', '', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'admin', NULL, 'admin', '2023-06-18 18:46:51', '2023-06-21 01:32:14', '9', '9', NULL, NULL, NULL, NULL, '<p>Cảnh quan thiên nhiên</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Sapa nổi tiếng với cảnh quan thiên nhiên tuyệt đẹp, bao gồm đồi núi, thung lũng xanh mướt, dòng sông mênh mông và thác nước. Núi Fansipan, được mệnh danh là \"Nóc nhà Đông Dương\", là điểm đến hấp dẫn cho những người yêu thích leo núi. Các thung lũng như thung lũng Mường Hoa, thung lũng Lao Chải và thung lũng Sapa là những địa điểm tuyệt vời để tham quan và khám phá.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/upload_images/images/2023/06/21/ruongbacthang5.jpg\" style=\"width: 100%;\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Văn hóa và dân tộc</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Với đa dạng văn hóa và dân tộc, Sapa là một điểm đến thú vị cho việc tìm hiểu về các dân tộc thiểu số. Dân tộc Dao, H&#39;Mong, Giáy và Tày là những dân tộc phổ biến trong khu vực này. Du khách có thể ghé thăm các bản làng dân tộc, tham gia các hoạt động văn hóa truyền thống và tận hưởng các món ăn đặc sản của dân tộc.</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/upload_images/images/2023/06/21/ruongbacthang2.jpg\" style=\"width: 100%;\" /></p>');
INSERT INTO `fs_destination` VALUES (3, 'hùa Một Cột nổi tiếng với kiến trúc độc đáo và phong cách kiến trúc độc nhất vô nhị. Ngôi chùa có hình dạng giống như một cây sen khổng lồ nổi trên một cột đá. Cột đá này được đặt trên một ao nước tượng trưng cho Hoàng Long (Rồng Vàng) của Việt Nam. Kiến trúc của chùa thể hiện sự kết hợp giữa kiến trúc châu Á và Ấn Độ giữa thế kỷ 11 và 12.', NULL, '', 1, 'ha-noi', 'Hà Nội', ',1,', ',ha-noi,', NULL, 'Chùa một cột', 'ha-noi-sapa-fansipan-lung-cu-ha-giang-3-ngay-2-dem', 'images/detination/2023/06/original/chuamotcot_1687285679.jpg', NULL, NULL, NULL, '2023-06-19 00:40:32', '2023-06-21 01:27:59', NULL, '1', NULL, NULL, NULL, NULL, '1', '3', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', '', '', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'admin', NULL, 'admin', '2023-06-19 00:45:58', '2023-06-21 01:27:59', '9', '9', NULL, NULL, NULL, NULL, '<h3>Chùa một cột</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Chùa Một Cột, còn được gọi là Chùa Diên Hựu, là một ngôi chùa nổi tiếng tọa lạc tại Hà Nội, Việt Nam. Được xây dựng vào thế kỷ thứ 11, chùa này được coi là biểu tượng quan trọng của văn hóa và kiến trúc truyền thống của Việt Nam.&nbsp;Chùa Một Cột là một trong những điểm tham quan nổi tiếng và thu hút đông đảo du khách. Du khách có thể thăm quan ngôi chùa và ngắm nhìn kiến trúc độc đáo của nó. Ngoài ra, chùa cũng là một nơi linh thiêng để cầu nguyện và tìm hiểu về đạo Phật.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/upload_images/images/2023/06/21/chuamotcot.jpg\" style=\"width: 564px; height: 871px;\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Chùa Một Cột là một di sản văn hóa quan trọng và là biểu tượng đặc trưng của Hà Nội. Với kiến trúc độc đáo và ý nghĩa lịch sử, nó thu hút sự quan tâm và ngưỡng mộ của du khách trong và ngoài nước.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Nhà thờ lớn</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhà thờ lớn Hà Nội, còn được gọi là Nhà thờ Đức Bà Hà Nội, là một trong những công trình kiến trúc tôn giáo nổi tiếng và đáng chú ý tại thủ đô Hà Nội, Việt Nam. Với vẻ đẹp và giá trị lịch sử, Nhà thờ lớn Hà Nội là một địa điểm du lịch quan trọng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/upload_images/images/2023/06/21/nhatholon.jpg\" style=\"width: 564px; height: 870px;\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kiến trúc độc đáo:&nbsp; Nhà thờ lớn Hà Nội được xây dựng theo kiến trúc Gothic châu Âu, mang đậm nét cổ điển và tráng lệ. Công trình có chiều dài khoảng 70m, chiều rộng gần 21m và chiều cao đến đỉnh tháp chính lên tới hơn 31m. Đáng chú ý là những cột cờ và những đồng hồ cổ trên tòa nhà, tạo nên một diện mạo vô cùng đặc biệt.</p>\r\n\r\n<p>Kiến trúc nội thất: Bên trong Nhà thờ lớn, khách tham quan có thể tìm hiểu về các tác phẩm nghệ thuật, bức tranh, tượng thánh và những vật phẩm tôn giáo quý giá. Nội thất của nhà thờ được trang trí tỉ mỉ và tinh xảo, tạo nên một không gian trang nghiêm và linh thiêng.</p>\r\n\r\n<p>Điểm tham quan và tôn giáo: Nhà thờ lớn Hà Nội là một địa điểm hấp dẫn cho du khách và người dân địa phương. Ngoài việc khám phá kiến trúc độc đáo và tìm hiểu về lịch sử, đây cũng là một nơi linh thiêng để cầu nguyện và tham dự các nghi lễ tôn giáo.</p>');
INSERT INTO `fs_destination` VALUES (4, 'Trải nghiệm dịch vụ nghỉ dưỡng 5* trên du thuyền đẳng cấp quốc tế. Giải trí cuối tuần với các hoạt động sôi nổi: Câu mực đêm, Chèo thuyền Kayak, tập dưỡng sinh Taichi. Thưởng ngoạn phong cảnh hùng vĩ như hòn Con Vịt, hòn Ngón tay , Hang Sáng Tối,… Đắm chìm giữa cảnh sắc thiên nhiên của Vịnh Lan Hạ. Thoả thích chơi golf hoặc thả mình thư giãn trong bể bơi bốn mùa trên tàu.', NULL, '', 2, 'hai-phong', 'Hải Phòng', ',2,', ',hai-phong,', NULL, 'Vịnh Hạ Long - Vịnh Lan Hạ', 'vinh-lan-ha-du-thuyen-5-stellar-of-the-sea-trai-nghiem-du-thuyen-cheo-thuyen-kayak-dao-khi3-ngay-2-dem', 'images/detination/2023/06/original/halong4_1687316961.jpg', NULL, NULL, NULL, '2023-07-05 00:55:14', '2023-06-21 10:09:21', NULL, '0', NULL, NULL, NULL, NULL, '1', '4', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', '', '', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'admin', NULL, 'admin', '2023-06-19 01:04:14', '2023-06-21 10:09:21', '9', '9', NULL, NULL, NULL, NULL, '<h3>Vịnh&nbsp; Lan Hạ</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Vịnh Lan Hạ Cát Bà có diện tích hơn 7000m2 với rất nhiều những hòn đảo lớn nhỏ, nằm về phía Đông của đảo Cát Bà và phía Nam của vịnh Hạ Long. Với vẻ đẹp hoang sơ, sinh động của những bãi tắm chưa được khai thác nhiều, vịnh Lan Hạ đang là điểm đến hấp dẫn các tín đồ du lịch. Ghé thăm vịnh Lan Hạ, du khách sẽ được tham gia các hoạt động vui chơi trên biển, khám phá cuộc sống của các làng chài nơi đây.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/upload_images/images/2023/06/19/halong1.jpg\" style=\"width: 100%;\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Chèo thuyền Kayak</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bạn nên mua một tour chèo thuyền kayak, những chiếc tàu du lịch sẽ đưa bạn đến các vùng biển lặng sóng và có cảnh quan đẹp để bạn vừa hoạt động thể thao vừa ngắm cảnh nơi đây. Thường các du khách hay chèo kayak ở khu vực Hang Tối Hang Sáng, Hang Me Kong…</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/upload_images/images/2023/06/19/halong7.jpg\" style=\"width: 100%;\" /></p>');
INSERT INTO `fs_destination` VALUES (5, 'Phố cổ Hội An là trái tim của thành phố, nơi tập trung nhiều kiến trúc cổ xưa và phong cách kiến trúc đa dạng. Với những con đường nhỏ nhắn, các ngôi nhà cổ truyền thống và các cây cầu nổi tiếng như Cầu Nhật Bản, Hội An mang trong mình vẻ đẹp yên bình và cổ kính.', NULL, '', 5, 'hoi-an', 'Hội An', ',5,', ',hoi-an,', NULL, 'Chùa cầu - phố cổ Hội An', 'vinh-lan-ha-du-thuyen-5-stellar-of-the-sea-trai-nghiem-du-thuyen-cheo-thuyen-kayak-dao-khi3-ngay-2-dem', 'images/detination/2023/06/original/phocohoian4_1687284979.jpg', NULL, NULL, NULL, '2023-07-08 01:13:21', '2023-06-21 01:18:28', NULL, '1', NULL, NULL, NULL, NULL, '1', '4', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', '', '', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'admin', NULL, 'admin', '2023-06-19 01:04:14', '2023-06-21 01:18:28', '9', '9', NULL, NULL, NULL, NULL, '<h3>Phố cổ Hội An</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Phố cổ Hội An là trái tim của thành phố, nơi tập trung nhiều kiến trúc cổ xưa và phong cách kiến trúc đa dạng. Với những con đường nhỏ nhắn, các ngôi nhà cổ truyền thống và các cây cầu nổi tiếng như Cầu Nhật Bản, Hội An mang trong mình vẻ đẹp yên bình và cổ kính.&nbsp;Hội An còn được biết đến với những ngôi nhà cổ truyền thống. Những ngôi nhà này thường được xây dựng bằng gỗ, có cấu trúc độc đáo và được trang trí bởi những tác phẩm nghệ thuật độc đáo. Du khách có thể thăm quan và tìm hiểu về lối sống và văn hóa truyền thống của người dân địa phương.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/upload_images/images/2023/06/21/phocohoian5.jpg\" style=\"width: 100%;\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Chùa Cầu</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Chùa Cầu là biểu tượng nổi tiếng của Hội An. Được xây dựng vào thế kỷ 17, chùa này kết hợp giữa kiến trúc châu Á và châu Âu và là điểm đến không thể bỏ qua khi ghé thăm Hội An.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/upload_images/images/2023/06/21/phocohoian3.jpg\" style=\"width: 100%;\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ẩm thực Hội An là một thiên đường ẩm thực với nhiều món ăn ngon độc đáo. Du khách có thể thưởng thức các món đặc sản như mì Quảng, bánh mỳ Phượng, cao lầu và các món hải sản tươi ngon.</p>');
INSERT INTO `fs_destination` VALUES (6, 'Cố đô Huế là nơi tọa lạc của Hoàng thành Huế, nơi cư trú của các hoàng đế triều Nguyễn. Với hệ thống các công trình kiến trúc như Cung điện Hoàng thành, Điện Thái Hòa, Điện Cần Chánh và Thế Miếu, du khách có thể khám phá văn hóa và lịch sử phong phú của triều đại Nguyễn.', NULL, '', 8, 'hue', 'Huế', ',8,', ',hue,', NULL, 'Cố đô Huế', 'vinh-lan-ha-du-thuyen-5-stellar-of-the-sea-trai-nghiem-du-thuyen-cheo-thuyen-kayak-dao-khi3-ngay-2-dem', 'images/detination/2023/06/original/codohue3_1687283927.jpg', NULL, NULL, NULL, '2023-07-05 00:55:14', '2023-06-21 01:08:08', NULL, '1', NULL, NULL, NULL, NULL, '1', '4', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', '', '', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'admin', NULL, 'admin', '2023-06-19 01:04:14', '2023-06-21 01:08:08', '9', '9', NULL, NULL, NULL, NULL, '<h3>Đại Nội</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đại Nội là nơi cư trú của hoàng hậu và gia đình hoàng gia. Nằm bên trong Hoàng thành, Đại Nội bao gồm các điểm tham quan như Điện Cung Thành, Điện Văn Miếu và Điện Hiển Lâm Các. Đây là nơi lưu giữ nhiều di vật và kỷ vật quý giá của triều đại Nguyễn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/upload_images/images/2023/06/21/codohue2.jpg\" style=\"width: 100%;\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Mỗi năm, Huế tổ chức Festival Huế - một sự kiện văn hóa lớn, thu hút nhiều du khách đến tham gia và khám phá nét đặc trưng văn hóa và nghệ thuật của vùng miền Trung.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/upload_images/images/2023/06/21/codohue.jpeg\" style=\"width: 800px; height: 450px;\" /></p>');
INSERT INTO `fs_destination` VALUES (7, 'Đà Lạt là một thành phố nằm ở cao nguyên Lâm Viên, tỉnh Lâm Đồng, miền Trung Việt Nam. Được biết đến với biệt danh \"thành phố của tình yêu\" hay \"thành phố mù sương\", Đà Lạt có khí hậu mát mẻ quanh năm và cảnh quan thiên nhiên tuyệt đẹp.', NULL, '', 9, 'da-lat', 'Đà Lạt', ',9,', ',da-lat,', NULL, 'Đà Lạt - Thành phố ngàn hoa rực rỡ', 'vinh-lan-ha-du-thuyen-5-stellar-of-the-sea-trai-nghiem-du-thuyen-cheo-thuyen-kayak-dao-khi3-ngay-2-dem', 'images/detination/2023/06/original/dalat_1687284016.jpg', NULL, NULL, NULL, '2023-07-05 00:55:14', '2023-06-21 01:04:12', NULL, '1', NULL, NULL, NULL, NULL, '1', '4', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', '', '', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'admin', NULL, 'admin', '2023-06-19 01:04:14', '2023-06-21 01:04:12', '9', '9', NULL, NULL, NULL, NULL, '<h3>Khí hậu và cảnh quan</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Với độ cao khoảng 1.500 mét trên mực nước biển, Đà Lạt có khí hậu mát mẻ và ôn đới quanh năm. Thành phố này nổi tiếng với sự đa dạng của các loại hoa, nhất là hoa hồng. Đà Lạt cũng có nhiều hồ, thác nước và rừng thông tạo nên cảnh quan tuyệt đẹp như Hồ Xuân Hương, Hồ Tuyền Lâm, thác Pongour và thác Datanla.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/upload_images/images/2023/06/21/dalat.jpg\" style=\"width: 100%;\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Mây Đà Lạt</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Một kiểu khí hậu đặc trưng tạo nên cái tên “xứ sở mù sương” cho nơi đây là sương mù bao quanh thành phố. Hiện tượng đặc biệt này hiếm nơi nào có được. Từ tháng 2 đến tháng 5 và từ tháng 9 đến tháng 10 hàng năm là thời điểm thường xuyên xuất hiện sương mù ở Đà Lạt. Sương mù thường sẽ tập trung vào lúc sáng sớm hoặc tối. Đà Lạt có địa thế cao và đây cũng chính là điều kiện lý tưởng để sương mù xuất hiện.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/upload_images/images/2023/06/21/dalat3.jpg\" style=\"width: 100%;\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/upload_images/images/2023/06/19/halong7.jpg\" style=\"width: 100%;\" /></p>');
INSERT INTO `fs_destination` VALUES (8, 'Trải nghiệm dịch vụ nghỉ dưỡng 5* trên du thuyền đẳng cấp quốc tế. Giải trí cuối tuần với các hoạt động sôi nổi: Câu mực đêm, Chèo thuyền Kayak, tập dưỡng sinh Taichi. Thưởng ngoạn phong cảnh hùng vĩ như hòn Con Vịt, hòn Ngón tay , Hang Sáng Tối,… Đắm chìm giữa cảnh sắc thiên nhiên của Vịnh Lan Hạ. Thoả thích chơi golf hoặc thả mình thư giãn trong bể bơi bốn mùa trên tàu.', NULL, '', 2, 'hai-phong', 'Hải Phòng', ',2,', ',hai-phong,', NULL, 'Vịnh Lan Hạ - Vịnh Hạ Long ', 'vinh-lan-ha-du-thuyen-5-stellar-of-the-sea-trai-nghiem-du-thuyen-cheo-thuyen-kayak-dao-khi3-ngay-2-dem', 'images/detination/2023/06/original/halong8_1687283697.jpg', NULL, NULL, NULL, '2023-07-05 00:55:14', '2023-06-21 00:54:57', NULL, '1', NULL, NULL, NULL, NULL, '1', '4', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', '', '', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'admin', NULL, 'admin', '2023-06-19 01:04:14', '2023-06-21 00:54:57', '9', '9', NULL, NULL, NULL, NULL, '<h3>Vịnh&nbsp; Lan Hạ</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Vịnh Lan Hạ Cát Bà có diện tích hơn 7000m2 với rất nhiều những hòn đảo lớn nhỏ, nằm về phía Đông của đảo Cát Bà và phía Nam của vịnh Hạ Long. Với vẻ đẹp hoang sơ, sinh động của những bãi tắm chưa được khai thác nhiều, vịnh Lan Hạ đang là điểm đến hấp dẫn các tín đồ du lịch. Ghé thăm vịnh Lan Hạ, du khách sẽ được tham gia các hoạt động vui chơi trên biển, khám phá cuộc sống của các làng chài nơi đây.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/upload_images/images/2023/06/19/halong1.jpg\" style=\"width: 100%;\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Chèo thuyền Kayak</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bạn nên mua một tour chèo thuyền kayak, những chiếc tàu du lịch sẽ đưa bạn đến các vùng biển lặng sóng và có cảnh quan đẹp để bạn vừa hoạt động thể thao vừa ngắm cảnh nơi đây. Thường các du khách hay chèo kayak ở khu vực Hang Tối Hang Sáng, Hang Me Kong…</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/upload_images/images/2023/06/19/halong7.jpg\" style=\"width: 100%;\" /></p>');
INSERT INTO `fs_destination` VALUES (9, 'Nha Trang là một thành phố ven biển nằm ở tỉnh Khánh Hòa, miền Trung Việt Nam. Với bãi biển trải dài, nước biển trong xanh và cảnh quan thiên nhiên tuyệt đẹp, Nha Trang đã trở thành một trong những điểm đến du lịch biển hàng đầu của Việt Nam.', NULL, '', 7, 'nha-trang', 'Nha Trang', ',7,', ',nha-trang,', NULL, 'Biển Nha Trang - Khánh Hòa', 'vinh-lan-ha-du-thuyen-5-stellar-of-the-sea-trai-nghiem-du-thuyen-cheo-thuyen-kayak-dao-khi3-ngay-2-dem', 'images/detination/2023/06/original/nhatrang4_1687283588.jpg', NULL, NULL, NULL, '2023-07-06 00:49:17', '2023-06-21 10:07:12', NULL, '1', NULL, NULL, NULL, NULL, '1', '4', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', '', '', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'admin', NULL, 'admin', '2023-06-19 01:04:14', '2023-06-21 10:07:12', '9', '9', NULL, NULL, NULL, NULL, '<h3>Biển Nha Trang</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nha Trang nổi tiếng với các bãi biển tuyệt đẹp như Bãi Dài, Bãi Trường, Bãi Dứa và Bãi Rừng. Các bãi biển ở đây có cát trắng mịn, nước biển trong xanh và độ sâu phù hợp cho các hoạt động như bơi lội, lặn biển và tham gia các môn thể thao nước khác.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/upload_images/images/2023/06/21/nhatrang4.jpg\" style=\"width: 100%;\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Đảo và du thuyền</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nha Trang có nhiều đảo và điểm du thuyền thú vị để khám phá như đảo Hòn Tre, đảo Bình Ba, đảo Hòn Mun và đảo Hòn Tằm. Du khách có thể thuê du thuyền hoặc tham gia tour du lịch đảo để khám phá cảnh quan đẹp, bãi biển hoang sơ và tận hưởng các hoạt động như lặn biển, câu cá và snorkeling.</p>\r\n\r\n<p>Không chỉ có bãi biển tuyệt đẹp, Nha Trang cũng có nhiều điểm tham quan và hoạt động nổi tiếng. Du khách có thể tham quan Tháp Bà Ponagar - một công trình kiến trúc Chăm cổ, tham gia thú vui tại công viên giải trí Vinpearl Land, và khám phá văn hóa địa phương tại Chợ Đầm.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/upload_images/images/2023/06/21/nhatrang2.jpg\" style=\"width: 100%;\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nha Trang nổi tiếng với ẩm thực đa dạng và hương vị biển tươi ngon. Du khách có thể thưởng thức các món hải sản tươi ngon, như nem nướng Nha Trang, chả cá Nha Trang và bún chả cá.</p>');
