INSERT INTO `fs_news` VALUES (914, 'LỊCH SINH HOẠT CUỐI TUẦN Năm học 2022-2023', '<div class=\"btgrid boarding-life\">\r\n<div class=\"row row-1\">\r\n<div class=\"col col-md-6\">\r\n<div class=\"content\">\r\n<h2>Morning:</h2>\r\n\r\n<div class=\"bd-bottom\">\r\n<h3>7h00:</h3>\r\n\r\n<p>Thức dậy</p>\r\n</div>\r\n\r\n<div class=\"bd-bottom\">\r\n<h3>8h00 to 9h00:</h3>\r\n\r\n<p>Ăn sáng</p>\r\n</div>\r\n\r\n<div class=\"bd-bottom\">\r\n<h3>9h00 to 10h00:</h3>\r\n\r\n<p>Dọn dẹp phòng &amp; giặt đồ</p>\r\n</div>\r\n\r\n<div class=\"bd-bottom\">\r\n<h3>10h00 to 12h00:</h3>\r\n\r\n<p>Hoạt động cá nhân</p>\r\n</div>\r\n\r\n<h3>12h00 to 13h00:</h3>\r\n\r\n<p>Ăn trưa</p>\r\n</div>\r\n</div>\r\n\r\n<div class=\"col col-md-6\">\r\n<div class=\"content\">\r\n<p><img alt=\"\" src=\"/upload_images/images/2022/11/10/THSchool_lang-hoa-lac-65-of.jpg\" style=\"width: 100%;\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/upload_images/images/2022/11/10/SON_6113.jpg\" style=\"width: 100%;\" /></p>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<div class=\"row row-2\">\r\n<div class=\"col col-md-6\">\r\n<div class=\"content\">\r\n<p><img alt=\"\" src=\"/upload_images/images/2022/11/10/THSchool_lang-hoa-lac.jpg\" style=\"width: 100%;\" /></p>\r\n</div>\r\n</div>\r\n\r\n<div class=\"col col-md-6\">\r\n<div class=\"content\">\r\n<h2>Buổi chiều:</h2>\r\n\r\n<div class=\"bd-bottom\">\r\n<h3>3h00 to 14h00:</h3>\r\n\r\n<p>Hoạt động giải trí (Chơi bi-a, xem phim...)</p>\r\n</div>\r\n\r\n<div class=\"bd-bottom\">\r\n<h3>14h00 to 17h00:</h3>\r\n\r\n<p>Chơi thể thao và tham gia các câu lạc bộ , hoạt động nấu ăn&nbsp;</p>\r\n</div>\r\n\r\n<h3>17h15 to 17h45:</h3>\r\n\r\n<p>Ăn tối</p>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<div class=\"row row-3\">\r\n<div class=\"col col-md-6\">\r\n<div class=\"content\">\r\n<h2>Buổi tối</h2>\r\n\r\n<div class=\"bd-bottom\">\r\n<h3>18h00 to 21h00:</h3>\r\n\r\n<p>Gọi điện về nhà, các hoạt động giải trí</p>\r\n</div>\r\n\r\n<div class=\"bd-bottom\">\r\n<h3>21h00 to 21h30:</h3>\r\n\r\n