INSERT INTO `fs_news_en` VALUES (896, '<p class=\"\">On Friday, December 4, we had over 300 performers on stage as we combined secondary school performances from our Chua Boc and Hoa Lac campuses for the Winter Concert 2020.</p>', '&nbsp;\n\n<h2 style=\"text-align:center;white-space:pre-wrap;\">On Friday, December 4, we had over 300 performers on stage as we combined secondary school performances from our Chua Boc and Hoa Lac campuses for the Winter Concert 2020.</h2>\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n  \n\n    \n  \n    <div\n        class=\"\n          image-block-outer-wrapper\n          layout-caption-below\n          design-layout-inline\n          combination-animation-none\n          individual-animation-none\n          individual-text-animation-none\n        \"\n        data-test=\"image-block-inline-outer-wrapper\"\n    >\n\n      \n\n      \n        <figure\n            class=\"\n              sqs-block-image-figure\n              intrinsic\n            \"\n            style=\"max-width:2048px;\"\n        >\n          \n        \n        \n\n        \n          \n            \n          <div\n              \n              \n              class=\"image-block-wrapper\"\n              data-animation-role=\"image\"\n              \n  \n\n          >\n            <div class=\"sqs-image-shape-container-element\n              \n          \n        \n              has-aspect-ratio\n            \" style=\"\n                position: relative;\n                \n                  padding-bottom:52.59117126464844%;\n                \n                overflow: hidden;\n              \"\n              >\n              <noscript><img src=\"https://images.squarespace-cdn.com/content/v1/59f286aa4c0dbf9244a43d28/1617264245298-PQ9G1KYHE7SY61DHHWUO/129009745_1748238085334618_5648477350722943651_n.jpg\" alt=\"129009745_1748238085334618_5648477350722943651_n.jpg\" /></noscript><img class=\"thumb-image\" src=\"https://images.squarespace-cdn.com/content/v1/59f286aa4c0dbf9244a