INSERT INTO `fs_news_en` VALUES (900, '<p class=\"\">November 4, 2021, the representatives of TH Group, TH Schools and Nghe An Department of Education and Training held a meeting with Huynh Thuc Khang High School to discuss having a collaboration between 2 schools, promising a new step for the T', '&nbsp;\n\n<h2 style=\"white-space:pre-wrap;\">November 4, 2021, the representatives of TH Group, TH Schools and Nghe An Department of Education and Training held a meeting with Huynh Thuc Khang High School to discuss having a collaboration between 2 schools, promising a new step for the TH School system.</h2>\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n  \n\n    \n  \n    <div\n        class=\"\n          image-block-outer-wrapper\n          layout-caption-below\n          design-layout-inline\n          combination-animation-none\n          individual-animation-none\n          individual-text-animation-none\n        \"\n        data-test=\"image-block-inline-outer-wrapper\"\n    >\n\n      \n\n      \n        <figure\n            class=\"\n              sqs-block-image-figure\n              intrinsic\n            \"\n            style=\"max-width:1280px;\"\n        >\n          \n        \n        \n\n        \n          \n            \n          <div\n              \n              \n              class=\"image-block-wrapper\"\n              data-animation-role=\"image\"\n              \n  \n\n          >\n            <div class=\"sqs-image-shape-container-element\n              \n          \n        \n              has-aspect-ratio\n            \" style=\"\n                position: relative;\n                \n                  padding-bottom:56.25%;\n                \n                overflow: hidden;\n              \"\n              >\n              <noscript><img src=\"https://images.squarespace-cdn.com/content/v1/59f286aa4c0dbf9244a43d28/e6129af7-8af2-4cef-b3f6-7a74dbff32b6/2021_11_05_11_10_301.png\" alt=\"\" /></noscript><img class=\"thumb-image\" src=\"https://images.sq