INSERT INTO `fs_news_en` VALUES (916, 'WEEK DAY ROUTINES Boarding House 2022 - 2023', '<h2 style=\"text-align: center;\">MORNING</h2>\r\n\r\n<div class=\"btgrid boarding-life\">\r\n<div class=\"row row-1\">\r\n<div class=\"col col-md-6\">\r\n<div class=\"content\">\r\n<p><img alt=\"\" src=\"/upload_images/images/2022/11/10/anh1.jpg\" style=\"width: 90%;\" /></p>\r\n\r\n<h3 style=\"text-align: center;\">6h30 to 6h45:</h3>\r\n\r\n<p style=\"text-align: center;\">Get up</p>\r\n</div>\r\n</div>\r\n\r\n<div class=\"col col-md-6\">\r\n<div class=\"content\">\r\n<p>&nbsp;</p>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<div class=\"row row-2\">\r\n<div class=\"col col-md-6\">\r\n<div class=\"content\">\r\n<p>&nbsp;</p>\r\n</div>\r\n</div>\r\n\r\n<div class=\"col col-md-6\">\r\n<div class=\"content\">\r\n<p><img alt=\"\" src=\"/upload_images/images/2022/11/10/anh2.jpg\" style=\"width: 90%;\" /></p>\r\n\r\n<h3 style=\"text-align: center;\">7h00 to 7h25:</h3>\r\n\r\n<p style=\"text-align: center;\">Collect laptops</p>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<div class=\"row row-3\">\r\n<div class=\"col col-md-6\">\r\n<div class=\"content\">\r\n<p><img alt=\"\" src=\"/upload_images/images/2022/11/10/anh3.jpg\" style=\"width: 90%;\" /></p>\r\n\r\n<h3 style=\"text-align: center;\">7h30 to 7h50:</h3>\r\n\r\n<p style=\"text-align: center;\">Breakfast&nbsp;</p>\r\n</div>\r\n</div>\r\n\r\n<div class=\"col col-md-6\">\r\n<div class=\"content\">\r\n<p>&nbsp;</p>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<div class=\"row row-4\">\r\n<div class=\"col col-md-6\">\r\n<div class=\"content\">\r\n<p>&nbsp;</p>\r\n</div>\r\n</div>\r\n\r\n<div class=\"col col-md-6\">\r\n<div class=\"content\">\r\n<p><img alt=\"\" src=\"/upload_images/images/2022/11/10/anh4.jpg\" style=\"width: 90%;\" /></p>\r\n\r\n<h3 style=\"text-align: center;\">7h50:</h3>\r\n\r\n<p style=\"text-align: center;\">Off to class</p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<h2 style=\"text-align: center;\"><br />\r\nAFTERNOON&nbsp;<br />\r\n&nbsp;</h2>\r\n\r\n<div class=\"btgrid boarding-life\">\r\n<div class=\"row row-1\">\r\n<div class=\"col col-md-6 bd-right\">\r\n<div class=\"content\">\r\n<div class=\"bd-bottom\">\r\n<h3>14h45:</h3>\r\n\r\n<p>Back to the boarding house (Students will have ECA at school till 15h45/ - Check in with staff&nbsp;</p>\r\n</div>\r\n\r\n<div class=\"bd-bottom\">\r\n<h3>15h00 to 17h00:</h3>\r\n\r\n<p>Sport fixtures or free time for arts and crafts, play with friends, laundry and&nbsp;hygiene activities</p>\r\n</div>\r\n\r\n<h3>17h00 -17h15:</h3>\r\n\r\n<p>Dinner call</p>\r\n</div>\r\n</div>\r\n\r\n<div class=\"col col-md-6\">\r\n<div class=\"content\">\r\n<p><img alt=\"\" src=\"/upload_images/images/2022/11/10/SON_6115.jpg\" style=\"width: 100%;\" /></p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<h2 style=\"text-align: center;\"><br />\r\nEVENING&nbsp;<br />\r\n&nbsp;</h2>\r\n\r\n<div class=\"btgrid boarding-life\">\r\n<div class=\"row row-1\">\r\n<div class=\"col col-md-6 bd-right\">\r\n<div class=\"content\">\r\n<div class=\"bd-bottom\">\r\n<h3>17h15 to 17h45:</h3>\r\n\r\n<p>Dinner&nbsp;</p>\r\n</div>\r\n\r\n<div class=\"bd-bottom\">\r\n<h3>18h00 to 19h45:</h3>\r\n\r\n<p>Preps routines</p>\r\n</div>\r\n\r\n<h3>19h45 to 20h30:</h3>\r\n\r\n<p>Late snack and phone time (calls to family and friends)&nbsp;</p>\r\n</div>\r\n</div>\r\n\r\n<div class=\"col col-md-6\">\r\n<div class=\"content\">\r\n<p><img alt=\"\" src=\"/upload_images/images/2022/11/10/Food-xmas-dessert.jpg\" style=\"width: 100%;\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<h2 style=\"text-align: center;\"><br />\r\nFor G9,10<br />\r\n&nbsp;</h2>\r\n\r\n<div class=\"btgrid boarding-life\">\r\n<div class=\"row row-1\">\r\n<div class=\"col col-md-6 bd-right\">\r\n<div class=\"content\">\r\n<div class=\"bd-bottom\">\r\n<h3>20h30 to 21h00:</h3>\r\n\r\n<p>Settling time for personal hygiene and bed routines&nbsp;</p>\r\n</div>\r\n\r\n<div class=\"bd-bottom\">\r\n<h3>21h00 to 21h30:</h3>\r\n\r\n<p>Quiet/reading time&nbsp;</p>\r\n</div>\r\n\r\n<h3>21h30</h3>\r\n\r\n<p>Light out - All students in bed and going to sleep</p>\r\n</div>\r\n</div>\r\n\r\n<div class=\"col col-md-6\">\r\n<div class=\"content\">\r\n<p><img alt=\"\" src=\"/upload_images/images/2022/11/10/Boys-dorm-desk.jpg\" style=\"width: 100%;\" /></p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<h2 style=\"text-align: center;\"><br />\r\nFor G11-12:<br />\r\n&nbsp;</h2>\r\n\r\n<div class=\"btgrid boarding-life\">\r\n<div class=\"row row-1\">\r\n<div class=\"col col-md-6 bd-right\">\r\n<div class=\"content\">\r\n<div class=\"bd-bottom\">\r\n<h3>20h30 to 22h00:</h3>\r\n\r\n<p>Further study</p>\r\n</div>\r\n\r\n<div class=\"bd-bottom\">\r\n<h3>22h00 to 22h15:</h3>\r\n\r\n<p>Settling time for personal hygiene and bed routines</p>\r\n</div>\r\n\r\n<div class=\"bd-bottom\">\r\n<h3>22h15 to 22h30:</h3>\r\n\r\n<p>Quiet/reading time&nbsp;</p>\r\n</div>\r\n\r\n<h3>22h30:</h3>\r\n\r\n<p>Light out</p>\r\n</div>\r\n</div>\r\n\r\n<div class=\"col col-md-6\">\r\n<div class=\"content\">\r\n<p><img alt=\"\" src=\"/upload_images/images/2022/11/10/Boys-dorm-room.jpg\" style=\"width: 100%;\" /></p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n<style type=\"text/css\">.boarding-life h2 {\r\n    color: #ed666f;\r\n    \r\n    font-weight: 500;\r\n	font-size: 30px;}\r\n.boarding-life h3 {\r\n	\r\n    font-weight: 500;\r\n    margin: 0.5rem 0;\r\n	font-size:24px;}\r\n\r\n.bd-bottom {\r\n	padding-bottom: 1rem;\r\n    border-bottom: 1px solid #ccc;}\r\n.boarding-life .content img { border-radius: 10px;}\r\n.boarding-life .col-md-6:first-child {\r\n	border-right: 1px solid #ccc;}\r\n</style>\r\n', NULL, '', 21, 'boarding-life', 'Boarding life', ',21,', ',boarding-life,', NULL, 'WEEK DAY ROUTINES Boarding House 2022 - 2023', 'week-day-routines-boarding-house-2022---2023', 'images/news/2022/11/original/candy-house-painting_1668134031.jpg', NULL, NULL, NULL, '2022-11-11 09:33:51', '2022-11-11 09:33:51', NULL, 0, 0, 0, 0, 0, 1, 326, NULL, 1, NULL, NULL, NULL, NULL, NULL, '', '', '', 0, 0, 0, 0, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'admin', NULL, NULL, '2022-11-11 09:33:51', '2022-11-11 09:33:51', 9, NULL, 0);
INSERT INTO `fs_news_en` VALUES (913, 'Within the framework of cooperation and development activities, from 22nd to 24th of April 2022, TH School system is honor to welcome the delegation including teachers, Secondary Principals. The delegation included: Mr. Phan Xuan Phan, Principal of Huynh ', '<p style=\"white-space:pre-wrap;\">Within the framework of cooperation and development activities,  22nd to 24th of April 2022, TH School system is honor to welcome the delegation including teachers, Secondary Principals. The delegation included: Mr. Phan Xuan Phan, Principal of Huynh Thuc Khang Secondary School; Mrs. Chu Thi Cam Van, Principal of Doi Cung Secondary School; Mr. Nguyen Van Binh, Principal of Hong Son Secondary School; Mr. Tran Viet Phuong, Principal of Le Loi Secondary School; Mrs. Nguyen Thi Thanh Thu, Principal of Le Mao Secondary School; Mrs. Nguyen Thi Le Hang, Principal of Vinh Tan Secondary School and Mr. Nguyen Hong Hai, Principal of Quang Trung Secondary School.&nbsp;</p>\r\n\r\n<p style=\"white-space:pre-wrap;\"><img alt=\"\" src=\"https://images.squarespace-cdn.com/content/v1/59f286aa4c0dbf9244a43d28/2faaa7dc-4f39-4b96-8f4c-5b0c995b4a3c/z3368990947021_c3714906ff2511202a803772aa2bcb82.jpg\" style=\"width: 100%;\" /></p>\r\n\r\n<p style=\"white-space:pre-wrap;\">The group of invited guests  the city. Vinh - Nghe An visited TH School</p>\r\n\r\n<p style=\"white-space:pre-wrap;\">FFrom the airport to Hanoi Center, Professor Nguyen Khanh Dieu Hong - Vietnamese Principal of TH School system welcomed the delegation. She constantly shared about the memories and the delicous foods that teachers in Vinh City introduce to TH School during business trips in order to deploy TH School Campus in Vinh City.</p>\r\n\r\n<p style=\"white-space:pre-wrap;\">Teachers and Principals were invited to attend \"Experience Day\" in TH School Hoa Lac Boarding campus. Only 30 minute drive  Hanoi center, TH School Hoa Lac impresses the delegation as a complelely different \"Fairy Garden\". Teachers immersed themselves in student activities such as: STEM, Music, Physical Education,etc... and had a lunch with boarding students and visited the world class boarding area.</p>\r\n\r\n<p style=\"white-space: pre-wrap; text-align: center;\"><img alt=\"\" class=\"thumb-image\" data-image=\"https://images.squarespace-cdn.com/content/v1/59f286aa4c0dbf9244a43d28/d968006d-3f1d-4fa4-bc34-ff3e5f676935/z3368991035313_8becd48fa6d234e96c9408de86169939.jpg\" data-image-dimensions=\"2076x1168\" data-image-focal-point=\"0.5,0.5\" data-image-id=\"6267611b7827e310590f7409\" data-load=\"false\" data-type=\"image\" src=\"https://images.squarespace-cdn.com/content/v1/59f286aa4c0dbf9244a43d28/d968006d-3f1d-4fa4-bc34-ff3e5f676935/z3368991035313_8becd48fa6d234e96c9408de86169939.jpg\" style=\"height: 563px; width: 1000px;\" /></p>\r\n\r\n<div class=\"image-block-outer-wrapper\r\n          layout-caption-below\r\n          design-layout-inline\r\n          combination-animation-none\r\n          individual-animation-none\r\n          individual-text-animation-none\r\n        \" data-test=\"image-block-inline-outer-wrapper\">\r\n<figure class=\"sqs-block-image-figure\r\n              intrinsic\r\n            \" style=\"max-width:2076px;\">\r\n<div class=\"image-block-wrapper\" data-animation-role=\"image\">\r\n<div class=\"sqs-image-shape-container-element\r\n              \r\n          \r\n        \r\n              has-aspect-ratio\r\n            \" style=\"\r\n                position: relative;\r\n                \r\n                  padding-bottom:56.26203918457031%;\r\n                \r\n                overflow: hidden;\r\n              \"><noscript><img src=\"https://images.squarespace-cdn.com/content/v1/59f286aa4c0dbf9244a43d28/d968006d-3f1d-4fa4-bc34-ff3e5f676935/z3368991035313_8becd48fa6d234e96c9408de86169939.jpg\" alt=\"\" /></noscript></div>\r\n</div>\r\n</figure>\r\n</div>\r\n\r\n<p style=\"white-space:pre-wrap;\">On the afternoon of April 22, Superintendent of TH School - Mr. Stephen West held a cozy professional discussion with teachers and Principals in Vinh City. The creative approach, the way to evoke student&#39;s potential, etc...and the different highlights of Cambridge Cirriculum were discussed ebullient by the teachers.</p>\r\n\r\n<p style=\"white-space:pre-wrap;\">The delegation continued to have one day experience and exchange at Bat Trang Pottery Village before coming back to Vinh City. Attending the trip with the delegation were Dr. Dang Thi Thu Thuy, Principal of Vietnam TH School Hoa Lac and Dr. Tran Dinh Chau, Deputy Head of the Steering Committee for Program and Textbook Innovation, Ministry of Education and Training; Member of General Education Committee, National Council of Education and Human Resource Development.</p>\r\n\r\n<p style=\"white-space: pre-wrap; text-align: center;\"><img alt=\"\" class=\"thumb-image\" data-image=\"https://images.squarespace-cdn.com/content/v1/59f286aa4c0dbf9244a43d28/662c05b7-2471-49af-a580-6353beaf8bdd/z3368993850718_d61628f54015de3c2f5624c9394f5ffc.jpg\" data-image-dimensions=\"1276x956\" data-image-focal-point=\"0.5,0.5\" data-image-id=\"6267602a74dad7348488df49\" data-load=\"false\" data-type=\"image\" src=\"https://images.squarespace-cdn.com/content/v1/59f286aa4c0dbf9244a43d28/662c05b7-2471-49af-a580-6353beaf8bdd/z3368993850718_d61628f54015de3c2f5624c9394f5ffc.jpg\" /></p>\r\n\r\n<p style=\"white-space: pre-wrap; text-align: center;\">The teachers had a visit to Bat Trang Ceramic Village</p>\r\n\r\n<p style=\"white-space:pre-wrap;\">&nbsp;</p>\r\n\r\n<p style=\"white-space:pre-wrap;\">With a constant schedule, School Teachers and Principals are always full of energy and joy in acitivites. TH School system, especially TH School Vinh campus is grateful for the valuable comments of teachers. Kindly quote the wishes of Mrs. Thanh Thu, Principal of Le Mai Secondary School: \"The pink color of Lotus in Ha Thanh land will overwhelm Nghe An in the coming days\". Hopefully with the good cooperation with schools, TH School Vinh will develop successfully and bring the best results to the students in the school.</p>\r\n\r\n<div class=\"image-block-outer-wrapper\r\n          layout-caption-below\r\n          design-layout-inline\r\n          combination-animation-none\r\n          individual-animation-none\r\n          individual-text-animation-none\r\n        \" data-test=\"image-block-inline-outer-wrapper\">\r\n<figure class=\"sqs-block-image-figure\r\n              intrinsic\r\n            \" style=\"max-width:1276px;\">\r\n<div class=\"image-block-wrapper\" data-animation-role=\"image\">\r\n<div class=\"sqs-image-shape-container-element\r\n              \r\n          \r\n        \r\n              has-aspect-ratio\r\n            \" style=\"\r\n                position: relative;\r\n                \r\n                  padding-bottom:74.921630859375%;\r\n                \r\n                overflow: hidden;\r\n              \"><noscript><img src=\"https://images.squarespace-cdn.com/content/v1/59f286aa4c0dbf9244a43d28/662c05b7-2471-49af-a580-6353beaf8bdd/z3368993850718_d61628f54015de3c2f5624c9394f5ffc.jpg\" alt=\"\" /></noscript></div>\r\n</div>\r\n</figure>\r\n</div>', NULL, '', 14, 'our-activities', 'Our activities', ',14,', ',our-activities,', NULL, ' TH School welcomes Principals from Vinh City to visit and exchange experience ', 'th-school-welcomes-principals--vinh-city-to-visit-and-exchange-experience', 'images/news/original/z3368991030177_00b7b58c39bc9344fa410e66f0cafa57.jpg', NULL, NULL, NULL, '2022-04-25 06:58:12', '2022-11-23 17:09:36', NULL, 0, 0, 0, 0, 37, 1, 323, NULL, 1, NULL, NULL, NULL, NULL, NULL, '', '', '', 0, 0, 0, 0, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'admin', NULL, '2022-11-23 17:09:36', NULL, 9, 0);
INSERT INTO `fs_news_en_copy1` VALUES (740, 'These are not old photos. This is what was happening today at Hoa Lac campus. 11th and 12th graders can now meet their teachers, classmates and take part in the interesting lessons in person after 6 months of online learning.', '<p>What we have seen was the excitement of students, teachers and staff, getting ready to catch up with the fun classes, have a chat or enjoy a football match.</p>\r\n\r\n<p>Mr. Stephen West, International Secondary Principal – Hoa Lac campus shared his thoughts: “I came here yesterday. I had dinner with the kids, talk to them to get to know them, and help Chua Boc students to familiarize with boarding as I do myself. It feels nice, and I think it’s important that the teachers and students see that I do it myself, not just the teachers only, so they would find themselves comfortable when returning to this second home.”</p>\r\n\r\n<p>&nbsp;</p>', NULL, '', 14, 'our-activities', 'Our activities', ',14,', ',our-activities,', NULL, 'Back to school after Winter break', 'back-to-school-after-winter-break', 'images/news/2022/08/original/7_1659431682.jpg', NULL, NULL, NULL, '2022-08-02 16:08:28', '2022-08-02 16:14:42', NULL, 1, 0, 0, 0, 0, 1, 1, NULL, 1, NULL, NULL, NULL, NULL, NULL, '', '', '', 0, 0, 0, 0, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'admin', NULL, 'admin', '2022-08-02 16:08:28', '2022-08-02 16:14:42', 9, 9, 0);
INSERT INTO `fs_news_en_copy1` VALUES (741, 'Năm học 2022-2023, trường THPT Huỳnh Thúc Kháng công bố kế hoạch tuyển sinh Hệ quốc tế theo chương trình hợp tác với hệ thống trường TH School. Đối tượng tuyển sinh là học sinh tốt nghiệp chương tr', '<h3><strong>1. CHỈ TIÊU TUYỂN SINH</strong></h3>\r\n\r\n<p>Lớp 10: 100 học sinh ( 4 lớp, mỗi lớp 25 học sinh)</p>\r\n\r\n<p><img alt=\"\" data-image=\"https://images.squarespace-cdn.com/content/v1/59f286aa4c0dbf9244a43d28/023dbfe3-1925-488e-a61c-f95eb9734e0b/page+6%402x.png\" data-image-dimensions=\"1191x1116\" data-image-focal-point=\"0.5,0.5\" data-image-id=\"62038baaf5c3032b2e4e5226\" data-image-resolution=\"500w\" data-load=\"false\" data-src=\"https://images.squarespace-cdn.com/content/v1/59f286aa4c0dbf9244a43d28/023dbfe3-1925-488e-a61c-f95eb9734e0b/page+6%402x.png\" data-type=\"image\" src=\"https://images.squarespace-cdn.com/content/v1/59f286aa4c0dbf9244a43d28/023dbfe3-1925-488e-a61c-f95eb9734e0b/page+6%402x.png?format=500w\" /></p>\r\n\r\n<h3><strong>2. CHƯƠNG TRÌNH HỌC</strong></h3>\r\n\r\n<p>Chương trình Quốc tế tại trường Huỳnh Thúc Kháng hợp tác với TH School là chương trình Trung học phổ thông Anh Quốc với 3 năm học lớp 10 - 11 - 12. Học sinh sẽ nhận được hai chứng chỉ Cambridge là Bằng Trung học Phổ thông Quốc tế Anh (IGCSE) (Chương trình lớp 10) và Bằng Tú tài Anh (A -Level) (Chương trình lớp 11 -12). Với các bằng cấp đạt được, học sinh sẽ sẵn sàng bước vào các trường đại học quốc tế.</p>\r\n\r\n<p><img alt=\"\" data-image=\"https://images.squarespace-cdn.com/content/v1/59f286aa4c0dbf9244a43d28/429f88d9-b9e6-4333-96c2-7438cf98e737/page+7%402x.png\" data-image-dimensions=\"1191x1114\" data-image-focal-point=\"0.5,0.5\" data-image-id=\"62038be517e7e151f88b7ef4\" data-image-resolution=\"500w\" data-load=\"false\" data-src=\"https://images.squarespace-cdn.com/content/v1/59f286aa4c0dbf9244a43d28/429f88d9-b9e6-4333-96c2-7438cf98e737/page+7%402x.png\" data-type=\"image\" src=\"https://images.squarespace-cdn.com/content/v1/59f286aa4c0dbf9244a43d28/429f88d9-b9e6-4333-96c2-7438cf98e737/page+7%402x.png?format=500w\" /></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Ở lớp 10, học sinh sẽ theo học chương trình IGCSE – Chương trình Trung học Phổ thông Quốc tế do Hội đồng khảo thí Cambridge, Anh Quốc cấp bằng và tích hợp các môn Việt Nam học với thời lượng 80% học bằng Tiếng Anh và 20% học bằng Tiếng Việt. Các môn học bằng tiếng Anh bao gồm Toán, Khoa học phức hợp, Kinh doanh và Tiếng Anh. Bên cạnh đó, các em sẽ học các môn học theo hình thức học dự án là Mỹ thuật hình ảnh, STEAM, Âm nhạc, Sản xuất phim… Học sinh sẽ vẫn duy trì và phát triển ngôn ngữ và nguồn gốc Việt nam với các môn học bao gồm Ngữ văn, Lịch sử, Địa lý, Giáo dục công dân.</p>\r\n	</li>\r\n	<li>\r\n	<p>Đối với khối 11 và 12, học sinh sẽ theo học Chương trình Tú tài Anh A – Levels, do Hội đồng khảo thí Cambridge, Anh Quốc cấp bằng. Học sinh sẽ chọn 3 đến 4 môn học phù hợp với định hướng chuyên ngành bậc đại học. Các môn học mà học sinh có thể chọn bao gồm Toán, Lý, Hóa, Kinh Doanh, Viễn cảnh Toàn cầu, Truyền thông số, Tâm lý học… Qua đó học sinh được trang bị kỹ lưỡng về kiến thức và kỹ năng, sẵn sàng bước vào các trường đại học trên toàn thế giới.&nbsp;Bên cạnh các môn học thuật, học sinh được trải nghiệm các môn học&nbsp;STEAM, Mỹ thuật, Âm nhạc, Sản xuất phim … nhằm giúp các em nâng cao các kỹ năng giải quyết vấn đề, ghi nhớ, sáng tạo và tư duy phản biện, chuẩn bị tốt nhất cho việc vững vàng bước vào bậc đại học và đạt được thành tựu tốt nhất trong cuộc sống.<br />\r\n	&nbsp;</p>\r\n	</li>\r\n</ul>\r\n\r\n<h3><strong>3. THỦ TỤC TUYỂN SINH</strong></h3>\r\n\r\n<p><img alt=\"\" data-image=\"https://images.squarespace-cdn.com/content/v1/59f286aa4c0dbf9244a43d28/e02675e1-7ce0-4e74-b5af-0e49d26f5f37/page+15%402x.png\" data-image-dimensions=\"1191x1684\" data-image-focal-point=\"0.5,0.5\" data-image-id=\"62038e9187d8f97c20e12a81\" data-image-resolution=\"500w\" data-load=\"false\" data-src=\"https://images.squarespace-cdn.com/content/v1/59f286aa4c0dbf9244a43d28/e02675e1-7ce0-4e74-b5af-0e49d26f5f37/page+15%402x.png\" data-type=\"image\" src=\"https://images.squarespace-cdn.com/content/v1/59f286aa4c0dbf9244a43d28/e02675e1-7ce0-4e74-b5af-0e49d26f5f37/page+15%402x.png?format=500w\" /></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><strong>BƯỚC 1 – THÔNG TIN TRƯỚC TUYỂN SINH</strong></p>\r\n\r\n	<p>Học sinh tham khảo thông tin về chương trình IGCSE và A levels, trao đổi kỹ lưỡng với Cán bộ tuyển sinh của nhà trường tại <a href=\"mailto:admissions@thschool.edu.vn\" tabindex=\"0\">admissions@thschool.edu.vn</a>, Tel: 0903260101 (Cô Quyên) hoặc 0919382255 (Thầy Long).</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>BƯỚC 2 – NỘP ĐƠN XIN NHẬP HỌC VÀ PHÍ ĐĂNG KÝ TUYỂN SINH</strong></p>\r\n\r\n	<ul>\r\n		<li>\r\n		<p>Hồ sơ dự tuyển bao gồm:</p>\r\n		</li>\r\n		<li>\r\n		<p>Đơn xin Nhập học (Do nhà trường cung cấp)</p>\r\n		</li>\r\n		<li>\r\n		<p>2 ảnh thẻ mới nhất</p>\r\n		</li>\r\n		<li>\r\n		<p>Bản sao giấy khai sinh</p>\r\n		</li>\r\n		<li>\r\n		<p>Bản sao Sổ hộ khẩu gia đình (Bản phô tô)</p>\r\n		</li>\r\n		<li>\r\n		<p>Học bạ (Bản sao)</p>\r\n		</li>\r\n		<li>\r\n		<p>Chứng chỉ IELTS/TOEFL (nếu có)</p>\r\n		</li>\r\n		<li>\r\n		<p>Thẻ bảo hiểm y tế hiện tại (Bản phô tô)</p>\r\n		</li>\r\n		<li>\r\n		<p>Phí đăng ký tuyển sinh</p>\r\n		</li>\r\n	</ul>\r\n\r\n	<p>Thời gian nộp hồ sơ: Từ nay đến khi nhà trường tuyển đủ 100 học sinh Khối 10 năm học 2022-2023.</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>BƯỚC 3 – KIỂM TRA ĐẦU VÀO</strong></p>\r\n\r\n	<p>Học sinh đăng ký lịch kiểm tra đầu vào và dự tuyển thi học bổng trong thời gian sớm nhất có thể để đảm bảo có chỗ. Nhà trường sẽ sắp xếp và thông báo thời gian và hình thức kiểm tra đến học sinh. Việc kiểm tra đầu vào và thi học bổng có thể được thực hiện online.</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>BƯỚC 4 – MỜI NHẬP HỌC</strong></p>\r\n\r\n	<p>Sau khi học sinh vượt qua phần kiểm tra đầu vào và thi học bổng. Nhà trường sẽ gửi thu mời nhập học và thư xác nhận học bổng đến học sinh.</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>BƯỚC 5 – XÁC NHẬN NHẬP HỌC VÀ ĐÓNG HỌC PHÍ</strong></p>\r\n\r\n	<ul>\r\n		<li>\r\n		<p>Trong vòng 2 tuần kể từ khi nhận được Thư mời nhập học, Phụ huynh sẽ xác nhận nhập học cho học sinh bằng cách đóng khoản lệ phí nhập học như thông báo của nhà trường</p>\r\n		</li>\r\n		<li>\r\n		<p>Học phí và tiền đặt cọc – đóng trước khi học sinh bắt đầu nhập học theo quy định về chính sách học phí năm học 2022-2023 TH School - Cơ sở Vinh</p>\r\n		</li>\r\n	</ul>\r\n\r\n	<p>Phụ huynh vui lòng tham khảo thông tin Chính sách học phí năm học 2022-2023 <a href=\"https://thschool.edu.vn/s/TH-School-Vinh-Nghe-An-Tuition-Policy-2022-23-EN-VIE.pdf\" tabindex=\"0\"><strong>tại đây</strong></a></p>\r\n	</li>\r\n</ul>\r\n\r\n<hr />\r\n<p><strong>Chi tiết xin liên hệ:</strong></p>\r\n\r\n<p>Phòng Tuyển sinh</p>\r\n\r\n<p>Hệ thống trường TH School</p>\r\n\r\n<p>Điện thoại: 091 938 2255&nbsp;</p>\r\n\r\n<p>Email: admissions@thschool.edu.vn</p>\r\n', NULL, '', 14, 'our-activities', 'Our activities', ',14,', ',our-activities,', NULL, 'TH School Vinh Campus 2022 - 2023 Enrollment', 'th-school-vinh-campus-2022---2023-enrollment', 'images/news/2022/08/original/d95229eac2b00fee56a1_1659431374.jpg', NULL, NULL, NULL, '2022-08-02 16:09:34', '2022-08-02 16:09:34', NULL, 1, 0, 0, 0, 0, 1, 2, NULL, 1, NULL, NULL, NULL, NULL, NULL, '', '', '', 0, 0, 0, 0, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'admin', NULL, NULL, '2022-08-02 16:09:34', '2022-08-02 16:09:34', 9, NULL, 0);
INSERT INTO `fs_news_en_copy1` VALUES (742, 'Xác định Nghệ An và Hà Tĩnh là mảnh đất của lòng hiếu học và là nơi nuôi nấng những nhân tài, Hệ thống trường TH School đã lựa chọn hai thành phố Vinh và Hà Tĩnh là điểm đến để tổ chức Hội', '<p>Tại TP. Vinh, Hội thảo “Chương trình THPT Cambridge Anh Quốc – Chìa khóa vàng giúp học sinh hội nhập” đã thu hút gần 100 phụ huynh, giáo viên và học sinh quan tâm. Đặc biệt, đây là sự kiện đầu tiên và có ý nghĩa to lớn để giới thiệu về TH School Cơ sở Vinh sẽ chính thức đi vào hoạt động từ năm học 2022 - 2023. Phát biểu tại hội thảo, Ông Đào Công Lợi, Phó GĐ Sở GD-ĐT Nghệ An hết sức đánh giá cao mô hình 80-20 được Bộ Giáo dục - Đào tạo cấp phép tại TH School. Mô hình 80-20 kết hợp chương trình Quốc tế Cambridge với tinh hoa Việt Nam học (tập trung vào tiếng Việt, Văn học, Lịch sử, Địa lí và Giáo dục Công Dân).</p>\r\n\r\n<p>Đến với TP. Hà Tĩnh, các phụ huynh và học sinh cũng đã được tìm hiểu về chương trình học quốc tế và mô hình nội trú khi tham gia Hội thảo “Sẵn sàng kiến tạo tương lai tại TH School Hòa lạc - trường nội trú đẳng cấp quốc tế”. Thầy Stephen West, Tổng hiệu trưởng Hệ thống trường TH School đã trực tiếp tham gia giúp phụ huynh và học sinh có cái nhìn tổng quan về phương pháp học tập, tầm quan trọng của các môn học trong chương trình IGCSE và A-Level và chứng chỉ quốc tế mà học sinh nhận được khi tốt nghiệp, cũng như môi trường lý tưởng cho các em rèn luyện tính tự lập, tự chủ từ những năm cấp 2 và cấp 3.</p>\r\n\r\n<p>Cả 2 sự kiện đều được đảm bảo công tác 5K. Cũng trong khuôn khổ của 2 sự kiện, học sinh tại Vinh và Hà Tĩnh có cơ hội tham gia dự thi học bổng lên đến 50% học phí của TH School. Bài thi học bổng tập trung vào kỹ năng tư duy logic, kỹ năng viết bằng tiếng Anh và phần phỏng vấn với đại diện của trường.</p>\r\n\r\n<p>TH School hi vọng rằng với nỗ lực lan tỏa giá trị của nền giáo dục toàn diện, các em học sinh Nghệ An, Hà Tĩnh nói riêng và các bạn học sinh trên khắp cả nước sẽ được tiếp cận một môi trường học tập nơi các em được phát triển về cả trí lực và thể lực, sẵn sàng bước vào những thử thách mới.</p>\r\n\r\n<p><a href=\"https://www.facebook.com/hashtag/thschool?__eep__=6&amp;__cft__[0]=AZWvrXBFewpv2_fjYSXXoFGalbuvliCngCfzZnFMcvWSLPGQyCyH4NJh8hinV-trcUJkXD5YOtH7SqS8SiYLQ1TJrgRgV4PLqNNDp0XzkMPc3dupPr_VRVj0CKX5yrWPIUxxTX5yQZysSeRG1IF2lMZ1Xp_Q8p3DAk-Lbdg6WmuAKagrJEgCDkbURB_u29JSvSM&amp;__tn__=*NK-R\" tabindex=\"0\">#thschool</a> <a href=\"https://www.facebook.com/hashtag/thschoolvinh?__eep__=6&amp;__cft__[0]=AZWvrXBFewpv2_fjYSXXoFGalbuvliCngCfzZnFMcvWSLPGQyCyH4NJh8hinV-trcUJkXD5YOtH7SqS8SiYLQ1TJrgRgV4PLqNNDp0XzkMPc3dupPr_VRVj0CKX5yrWPIUxxTX5yQZysSeRG1IF2lMZ1Xp_Q8p3DAk-Lbdg6WmuAKagrJEgCDkbURB_u29JSvSM&amp;__tn__=*NK-R\" tabindex=\"0\">#thschoolvinh</a> <a href=\"https://www.facebook.com/hashtag/hoalaccampus?__eep__=6&amp;__cft__[0]=AZWvrXBFewpv2_fjYSXXoFGalbuvliCngCfzZnFMcvWSLPGQyCyH4NJh8hinV-trcUJkXD5YOtH7SqS8SiYLQ1TJrgRgV4PLqNNDp0XzkMPc3dupPr_VRVj0CKX5yrWPIUxxTX5yQZysSeRG1IF2lMZ1Xp_Q8p3DAk-Lbdg6WmuAKagrJEgCDkbURB_u29JSvSM&amp;__tn__=*NK-R\" tabindex=\"0\">#hoalaccampus</a> <a href=\"https://www.facebook.com/hashtag/conference?__eep__=6&amp;__cft__[0]=AZWvrXBFewpv2_fjYSXXoFGalbuvliCngCfzZnFMcvWSLPGQyCyH4NJh8hinV-trcUJkXD5YOtH7SqS8SiYLQ1TJrgRgV4PLqNNDp0XzkMPc3dupPr_VRVj0CKX5yrWPIUxxTX5yQZysSeRG1IF2lMZ1Xp_Q8p3DAk-Lbdg6WmuAKagrJEgCDkbURB_u29JSvSM&amp;__tn__=*NK-R\" tabindex=\"0\">#conference</a> <a href=\"https://www.facebook.com/hashtag/vinh?__eep__=6&amp;__cft__[0]=AZWvrXBFewpv2_fjYSXXoFGalbuvliCngCfzZnFMcvWSLPGQyCyH4NJh8hinV-trcUJkXD5YOtH7SqS8SiYLQ1TJrgRgV4PLqNNDp0XzkMPc3dupPr_VRVj0CKX5yrWPIUxxTX5yQZysSeRG1IF2lMZ1Xp_Q8p3DAk-Lbdg6WmuAKagrJEgCDkbURB_u29JSvSM&amp;__tn__=*NK-R\" tabindex=\"0\">#vinh</a> <a href=\"https://www.facebook.com/hashtag/hatinh?__eep__=6&amp;__cft__[0]=AZWvrXBFewpv2_fjYSXXoFGalbuvliCngCfzZnFMcvWSLPGQyCyH4NJh8hinV-trcUJkXD5YOtH7SqS8SiYLQ1TJrgRgV4PLqNNDp0XzkMPc3dupPr_VRVj0CKX5yrWPIUxxTX5yQZysSeRG1IF2lMZ1Xp_Q8p3DAk-Lbdg6WmuAKagrJEgCDkbURB_u29JSvS