INSERT INTO `fs_news_en_copy1` VALUES (747, 'Being in TH definitely helped me a lot with that and with getting other achievements as well”\r\nThe sharing  the students is the greatest encouragement, therefore teachers have more motivation to create more interesting lessons.', '<p>\"Mr. N, I have great news!</p>\r\n\r\n<p>I just got an 8.5 overall for my IELTS</p>\r\n\r\n<p>Being in TH definitely helped me a lot with that and with getting other achievements as well”</p>\r\n\r\n<p>The sharing  the students is the greatest encouragement, therefore teachers have more motivation to create more interesting lessons.</p>\r\n\r\n<p>Using different teaching methods that gives students a lot of inspiration to learn, then achieving many achievements.</p>\r\n\r\n<p><img alt=\"\" src=\"/upload_images/images/2022/08/03/264097960_2027810720710685_1913434923828822859_n.jpg\" style=\"height:1200px; width:1200px\" /></p>', NULL, '', 14, 'our-activities', 'Our activities', ',14,', ',our-activities,', NULL, 'Tran Huy Duc Bao - student of TH School just got 8.5 IELTS', 'tran-huy-duc-bao-student-of-th-school-just-got-85-ielts', 'images/news/2022/08/original/264097960_2027810720710685_1913434923828822859_n_1659496496.jpg', NULL, NULL, NULL, '2022-08-03 10:14:56', '2022-08-19 09:18:42', NULL, 0, 0, 0, 0, 1, 1, 1, NULL, 1, NULL, NULL, NULL, NULL, NULL, '', '', '', 0, 0, 0, 0, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'admin', NULL, 'admin', '2022-08-03 10:14:56', '2022-08-19 09:18:42', 9, 9, 0);
