INSERT INTO `fs_news_en_copy1` VALUES (756, 'WEEK DAY ROUTINES Boarding House 2022 - 2023', '<h2 style=\"text-align: center;\">MORNING</h2>\r\n\r\n<div class=\"btgrid boarding-life\">\r\n<div class=\"row row-1\">\r\n<div class=\"col col-md-6\">\r\n<div class=\"content\">\r\n<p><img alt=\"\" src=\"/upload_images/images/2022/11/10/anh1.jpg\" style=\"width: 90%;\" /></p>\r\n\r\n<h3 style=\"text-align: center;\">6h30 to 6h45:</h3>\r\n\r\n<p style=\"text-align: center;\">Get up</p>\r\n</div>\r\n</div>\r\n\r\n<div class=\"col col-md-6\">\r\n<div class=\"content\">\r\n<p>&nbsp;</p>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<div class=\"row row-2\">\r\n<div class=\"col col-md-6\">\r\n<div class=\"content\">\r\n<p>&nbsp;</p>\r\n</div>\r\n</div>\r\n\r\n<div class=\"col col-md-6\">\r\n<div class=\"content\">\r\n<p><img alt=\"\" src=\"/upload_images/images/2022/11/10/anh2.jpg\" style=\"width: 90%;\" /></p>\r\n\r\n<h3 style=\"text-align: center;\">7h00 to 7h25:</h3>\r\n\r\n<p style=\"text-align: center;\">Collect laptops</p>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<div class=\"row row-3\">\r\n<div class=\"col col-md-6\">\r\n<div class=\"content\">\r\n<p><img alt=\"\" src=\"/upload_images/images/2022/11/10/anh3.jpg\" style=\"width: 90%;\" /></p>\r\n\r\n<h3 style=\"text-align: center;\">7h30 to 7h50:</h3>\r\n\r\n<p style=\"text-align: center;\">Breakfast&nbsp;</p>\r\n</div>\r\n</div>\r\n\r\n<div class=\"col col-md-6\">\r\n<div class=\"content\">\r\n<p>&nbsp;</p>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<div class=\"row row-4\">\r\n<div class=\"col col-md-6\">\r\n<div class=\"content\">\r\n<p>&nbsp;</p>\r\n</div>\r\n</div>\r\n\r\n<div class=\"col col-md-6\">\r\n<div class=\"content\">\r\n<p><img alt=\"\" src=\"/upload_images/images/2022/11/10/anh4.jpg\" style=\"width: 90%;\" /></p>\r\n\r\n<h3 style=\"text-align: center;\">7h50:</h3>\r\n\r\n<p style=\"text-align: center;\">Off to class</p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<h2 style=\"text-align: center;\"><br />\r\nAFTERNOON&nbsp;<br />\r\n&nbsp;</h2>\r\n\r\n<div class=\"btgrid boarding-life\">\r\n<div class=\"row row-1\">\r\n<div class=\"col col-md-6 bd-right\">\r\n<div class=\"content\">\r\n<div class=\"bd-bottom\">\r\n<h3>14h45:</h3>\r\n\r\n<p>Back to the boarding house (Students will have ECA at school till 15h45/ - Check in with staff&nbsp;</p>\r\n</div>\r\n\r\n<div class=\"bd-bottom\">\r\n<h3>15h00 to 17h00:</h3>\r\n\r\n<p>Sport fixtures or free time for arts and crafts, play with friends, laundry and&nbsp;hygiene activities</p>\r\n</div>\r\n\r\n<h3>17h00 -17h15:</h3>\r\n\r\n<p>Dinner call</p>\r\n</div>\r\n</div>\r\n\r\n<div class=\"col col-md-6\">\r\n<div class=\"content\">\r\n<p><img alt=\"\" src=\"/upload_images/images/2022/11/10/SON_6115.jpg\" style=\"width: 100%;\" /></p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<h2 style=\"text-align: center;\"><br />\r\nEVENING&nbsp;<br />\r\n&nbsp;</h2>\r\n\r\n<div class=\"btgrid boarding-life\">\r\n<div class=\"row row-1\">\r\n<div class=\"col col-md-6 bd-right\">\r\n<div class=\"content\">\r\n<div class=\"bd-bottom\">\r\n<h3>17h15 to 17h45:</h3>\r\n\r\n<p>Dinner&nbsp;</p>\r\n</div>\r\n\r\n<div class=\"bd-bottom\">\r\n<h3>18h00 to 19h45:</h3>\r\n\r\n<p>Preps routines</p>\r\n</div>\r\n\r\n<h3>19h45 to 20h30:</h3>\r\n\r\n<p>Late snack and phone time (calls to family and friends)&nbsp;</p>\r\n</div>\r\n</div>\r\n\r\n<div class=\"col col-md-6\">\r\n<div class=\"content\">\r\n<p><img alt=\"\" src=\"/upload_images/images/2022/11/10/Food-xmas-dessert.jpg\" style=\"width: 100%;\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<h2 style=\"text-align: center;\"><br />\r\nFor G9,10<br />\r\n&nbsp;</h2>\r\n\r\n<div class=\"btgrid boarding-life\">\r\n<div class=\"row row-1\">\r\n<div class=\"col col-md-6 bd-right\">\r\n<div class=\"content\">\r\n<div class=\"bd-bottom\">\r\n<h3>20h30 to 21h00:</h3>\r\n\r\n<p>Settling time for personal hygiene and bed routines&nbsp;</p>\r\n</div>\r\n\r\n<div class=\"bd-bottom\">\r\n<h3>21h00 to 21h30:</h3>\r\n\r\n<p>Quiet/reading time&nbsp;</p>\r\n</div>\r\n\r\n<h3>21h30</h3>\r\n\r\n<p>Light out - All students in bed and going to sleep</p>\r\n</div>\r\n</div>\r\n\r\n<div class=\"col col-md-6\">\r\n<div class=\"content\">\r\n<p><img alt=\"\" src=\"/upload_images/images/2022/11/10/Boys-dorm-desk.jpg\" style=\"width: 100%;\" /></p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<h2 style=\"text-align: center;\"><br />\r\nFor G11-12:<br />\r\n&nbsp;</h2>\r\n\r\n<div class=\"btgrid boarding-life\">\r\n<div class=\"row row-1\">\r\n<div class=\"col col-md-6 bd-right\">\r\n<div class=\"content\">\r\n<div class=\"bd-bottom\">\r\n<h3>20h30 to 22h00:</h3>\r\n\r\n<p>Further study</p>\r\n</div>\r\n\r\n<div class=\"bd-bottom\">\r\n<h3>22h00 to 22h15:</h3>\r\n\r\n<p>Settling time for personal hygiene and bed routines</p>\r\n</div>\r\n\r\n<div class=\"bd-bottom\">\r\n<h3>22h15 to 22h30:</h3>\r\n\r\n<p>Quiet/reading time&nbsp;</p>\r\n</div>\r\n\r\n<h3>22h30:</h3>\r\n\r\n<p>Light out</p>\r\n</div>\r\n</div>\r\n\r\n<div class=\"col col-md-6\">\r\n<div class=\"content\">\r\n<p><img alt=\"\" src=\"/upload_images/images/2022/11/10/Boys-dorm-room.jpg\" style=\"width: 100%;\" /></p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n<style type=\"text/css\">.boarding-life h2 {\r\n    color: #ed666f;\r\n    \r\n    font-weight: 500;\r\n	font-size: 30px;}\r\n.boarding-life h3 {\r\n	\r\n    font-weight: 500;\r\n    margin: 0.5rem 0;\r\n	font-size:24px;}\r\n\r\n.bd-bottom {\r\n	padding-bottom: 1rem;\r\n    border-bottom: 1px solid #ccc;}\r\n.boarding-life .content img { border-radius: 10px;}\r\n.boarding-life .col-md-6:first-child {\r\n	border-right: 1px solid #ccc;}\r\n</style>', NULL, '', 21, 'boarding-life', 'Boarding life', ',21,', ',boarding-life,', NULL, 'WEEK DAY ROUTINES Boarding House 2022 - 2023', 'week-day-routines-boarding-house-2022-2023', 'images/news/2022/11/original/_dsc7143_1668048954.jpg', NULL, NULL, NULL, '2022-11-10 09:55:54', '2022-11-10 11:54: